import pywhatkit as kitimport pyautoguiimport time# Your target phone number and messagephone_number = "+917859880586"message = "Marketing boyğŸ¤£ğŸ¤£"# Number of times to send the messagerepeat_count = 50# Delay between each message in secondsdelay_between_messages = 0.5  # Adjust as needed# Open WhatsApp web and send the first message to get the window in focuskit.sendwhatmsg_instantly(phone_number, message, wait_time=3)time.sleep(3)  # Wait for WhatsApp web to open and send the first messagefor _ in range(repeat_count - 1):    pyautogui.typewrite(message)    pyautogui.press('enter')    time.sleep(delay_between_messages)  # Sleep to avoid sending all at onceprint("Messages sent.")