from datetime import datetimeimport pywhatkitimport pyttsx3engine = pyttsx3.init("sapi5")voices = engine.getProperty("voices")engine.setProperty("voice", voices[2].id)rate = engine.setProperty("rate", 170)def speak(audio):    engine.say(audio)    engine.runAndWait()def send_whatsapp_message():    speak("What's the message?")    message = str(input("Enter the message: "))    receiver_phone = "+919773069326"  # Replace with the actual phone number    try:        now = datetime.now()        pywhatkit.sendwhatmsg(receiver_phone, message, now.hour, now.minute + 2, tab_close=True)        speak("Message sent successfully.")    except Exception as e:        print(f"Error: {e}")        print("Failed to send the message. Please try again later.")# Main program